mapper.select:
select comment.id,username,content from comment,consumer
where user_id = consumer.id
and song_id = #{id}

mapper.update:
update from comment
set song_id=null
where id = #{id}

mapper.select:
select comment.id,username,user_id,location,comment.create_time,content,avator,type,show1,up,up_user from comment,consumer
where user_id = consumer.id
and song_list_id = #{id}

mapper.update:
update from comment
set song_list_id=null
where id = #{id}

mapper.select:
select comment.id,username,user_id,location,comment.create_time,content,avator,type,show1,up,up_user from comment,consumer
where user_id = consumer.id
and mv_id = #{id}
